<?php use_helper('Text') ?><div class="page-header">  <h1>Dashboard → Comments</h1></div><div class="page-content row-fluid tabbable tabs-left">  <?php include_partial('dashboard-tabs') ?>  <section class="tab-content">    <div class="btn-toolbar">      <div class="btn-group"><?php $state = $sf_context->getRequest()->getParameter('state') ?>        <a class="btn<?php if ('comments' == $state): ?> active<?php endif ?>" href="<?php echo url_for('@dashboard-comments?state=comments') ?>">Комментарии</a>        <a class="btn<?php if ('all' == $state): ?> active<?php endif ?>" href="<?php echo url_for('@dashboard-comments?state=all') ?>">Всё</a>      </div>    </div>    <ul><?php foreach ($comments as $comment): ?>    <?php if ($comment->getChangedTicketStateTo()): ?>      <li>        <a href="<?php echo ($url=url_for('@tickets-show?id=' . $comment->getTicketId()) . '#ticketcomment-' . $comment->getId()) ?>"><?php echo $comment->getTicketId() ?></a>:        <a href="<?php echo $url ?>"><?php echo $comment->getText() ?></a>        — @<?php echo $comment->getCreator()->getUsername() ?> <?php echo $comment->getChangedTicketStateTo() ?>      </li>    <?php else: ?>      <li>        <a href="<?php echo ($url=url_for('@tickets-show?id=' . $comment->getTicketId()) . '#ticketcomment-' . $comment->getId()) ?>"><?php echo $comment->getTicketId() ?></a>:        <a href="<?php echo $url ?>"><?php echo truncate_text($comment->getText(), 80) ?></a>        — @<?php echo $comment->getCreator()->getUsername() ?>      </li>    <?php endif ?>    <?php endforeach ?></ul>  </section></div>