<div class="page-header">  <h1>Dashboard → Tickets</h1></div><div class="page-content row-fluid tabbable tabs-left">  <?php include_partial('dashboard-tabs') ?>  <section class="tab-content">    <div class="btn-toolbar">      <div class="btn-group"><?php $state = $sf_context->getRequest()->getParameter('state') ?>        <a class="btn<?php if ('opened' == $state): ?> active<?php endif ?>" href="<?php echo url_for('@dashboard-tickets') ?>">Открытые</a>        <a class="btn<?php if ('closed' == $state): ?> active<?php endif ?>" href="<?php echo url_for('@dashboard-tickets?state=closed') ?>">Закрытые</a>        <a class="btn<?php if ('all' == $state): ?> active<?php endif ?>" href="<?php echo url_for('@dashboard-tickets?state=all') ?>">Все</a>      </div>    </div>    <?php include_partial('global/tickets-list', array(      'tickets' => $pager->getResults(),      'showDate' => true,      'showDeadline' => true,      'showCompanyName' => true,      'showUserName' => true    )) ?>    <?php if ($pager->haveToPaginate()): ?>    <div class="pagination">      <ul>        <li<?php echo ($pager->getPreviousPage() == $pager->getPage()) ? ' class="active"' : '' ?>>          <a href="<?php echo url_for('@dashboard-tickets?state=' . $state . '&page=' . $pager->getPreviousPage()) ?>">&lt;</a>        </li>      <?php foreach ($pager->getLinks() as $page): ?>        <li<?php echo ($page == $pager->getPage()) ? ' class="active"' : '' ?>>          <a href="<?php echo url_for('@dashboard-tickets?state=' . $state . '&page=' . $page) ?>"><?php echo $page ?></a>        </li>      <?php endforeach; ?>        <li<?php echo ($pager->getNextPage() == $pager->getPage()) ? ' class="active"' : '' ?>>          <a href="<?php echo url_for('@dashboard-tickets?state=' . $state . '&page=' . $pager->getNextPage()) ?>">&gt;</a>        </li>      </ul>    </div>    <?php endif; ?>  </section></div>