<?php

/**
 * Ticket
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 *
 * @package    helpdesk
 * @subpackage model
 * @author     Anatoly Pashin
 * @version    SVN: $Id: Builder.php 7490 2010-03-29 19:53:27Z jwage $
 */
class Ticket extends BaseTicket
{
  private function correctDeadline() {
    $deadline = $this->getDeadline();
    if ($deadline) {
      $this->setDeadline(date('Y.m.d 23:59', strtotime($deadline)));
    }
  }

  public function preInsert($event) {
    $this->correctDeadline();
  }

  public function preUpdate($event) {
    $this->correctDeadline();
  }

  public function getCloser() {
    return Doctrine_Query::create()
      ->from('Comment c')
      ->addWhere('c.ticket_id = ?', $this->getId())
      ->addWhere('c.changed_ticket_state_to = ?', 'closed')
      ->leftJoin('c.Creator')
      ->limit(1)
      ->addOrderBy('c.created_at desc')
      ->fetchOne()
    ;
  }

  public function getApplier() {
    return Doctrine_Query::create()
      ->from('Comment c')
      ->addWhere('c.ticket_id = ?', $this->getId())
      ->addWhere('c.changed_ticket_state_to = ?', 'applied')
      ->leftJoin('c.Creator')
      ->limit(1)
      ->addOrderBy('c.created_at desc')
      ->fetchOne()
    ;
  }

  public function postInsert($event) {
    $company = $this->getCreator()->getGroups()->getFirst();

    // send sms
    if ($company and true == ($notify = $company->getNotify())) {
      $phones = [];
      foreach ($notify as $user) {
        if ($user->getPhone()) {
          $phones[] = $user->getPhone();
        }
      }

      Sms::send($phones, 'Заявка от компании ' . $company->getName() . ', пользователь ' . $this->getCreator()->getUsername());
    }

    // send email to creator
    $to = $this->getRealSender() ? $this->getRealSender() : $this->getCreator()->getEmailAddress();
    if ($to !== 'support@helpdesk.f1lab.ru') {

      $message = '
В системе зарегистрировано Обращение № ' . $this->getId() . '
Время создания: ' . date('d.m.Y H:i:s', strtotime($this->getCreatedAt())) . '
Тема: ' . $this->getName() . '
Описание: ' . str_replace(['--', '<br/>'], '', $this->getDescription()) . '

В ближайшее время Заявка будет рассмотрена!

Если вы хотите что-то дополнить, то можете ответить на это письмо
или сделать это онлайн на странице http://helpdesk.f1lab.ru/tickets/' . $this->getId() . '

--
С уважением, команда F1 Lab
';
      Email::send($to, '[F1LAB-HLPDSK-' . $this->getId() . '] ' . $this->getName(), $message);
    }
  }
}
